mapper.update:
update details set ordercode=#{ordercode} , jiancaiid=#{jiancaiid} , num=#{num} ,
price=#{price} , cityid=#{cityid} , peihuoid=#{peihuoid} ,
viewdate=#{viewdate} where detailsid=#{detailsid}

mapper.select:
select a.* , b.jiancainame , c.cityname , d.peihuoname from details a ,
jiancai b , city c , peihuo d where 1=1 and a.jiancaiid = b.jiancaiid and
a.cityid = c.cityid and a.peihuoid = d.peihuoid

	and a.ordercode = #{ordercode}


	and a.jiancaiid = #{jiancaiid}


	and a.num = #{num}


	and a.price = #{price}


	and a.cityid = #{cityid}


	and a.peihuoid = #{peihuoid}


	and a.viewdate = #{viewdate}
